import React, { useState, useEffect } from 'react';
import { Loader } from "semantic-ui-react";
import { size, map } from "lodash";
import { Course } from "../../../../api";
import { useAuth } from "../../../../hooks";
//import { UserItem } from "../UserItem";

const courseController = new Course();

export  function ListCourses(props) {

  const { reload, onReload } = props;
  const [courses, setCourses] = useState(null);
  const { accessToken } = useAuth();


  useEffect(() => {
    (async () => {
      try {
        setCourses(null);
        const response = await courseController.getCourses(accessToken);
        setCourses(response);
      } catch (error) {
        console.error(error);
      }
    })()
  }, [reload])

  if(!courses) return <Loader active inline="centered"/>
  if(size(courses) === 0) return "Não existem nenhum usuário cadastrado!";

 // return map(courses, (uscourseer) => <CourseItem key={user._id} user={user} onReload={onReload}/>)
}
