import React from 'react';
import { Form } from "semantic-ui-react";
import { useFormik } from "formik";
import { Menu } from "../../../../api";
import { useAuth } from "../../../../hooks";
import { initialValues, validationSchema } from "./MenuForm.form";
import "./UserForm.scss";


const menuController = new Menu();

export function MenuForm(props) {
  
  const { close, onReload, menu } = props;
  //console.log(props);
//console.log(onReload());
  //console.log(close);
  const { accessToken } = useAuth();

  const formik = useFormik({
    initialValues: initialValues(menu),
    validationSchema: validationSchema(menu),
    validateOnChange: false,
    onSubmit: async (formValue) => {
        try {
            if(!menu){
                await menuController.createMenu(accessToken, formValue);
            }else{
               await menuController.updateMenu(accessToken, menu._id , formValue);
            }
            onReload();
            close();
        } catch (error) {
            console.error(error);
        }
    }
  });


  return (
    <Form className='menu-form' onSubmit={formik.handleSubmit}>

    <Form.Group widths="equal">
        <Form.Input 
            name="title" 
            placeholder="TÃ­tulo"
            onChange={formik.handleChange}
            value={formik.values.title}
            error={formik.errors.title}
        />
        <Form.Input 
            name="path" 
            placeholder="Path" 
            onChange={formik.handleChange}
            value={formik.values.title}
            error={formik.errors.title}
        />
    </Form.Group>


    <Form.Group widths="equal">
        <Form.Input 
            name="order" 
            placeholder="Ordem"
            onChange={formik.handleChange}
            value={formik.values.order}
            error={formik.errors.order}
    />
    </Form.Group>


    <Form.Button type="submit"  primary fluid  loading={ formik.isSubmitting }>
        {menu ? "Atualizar menu" : "Criar menu"}
    </Form.Button>

    </Form>
  );
}
