import React, { useState } from 'react';
import { Image, Button, Icon, Confirm } from 'semantic-ui-react';
import { Course } from '../../../../api';
import { image } from "../../../../assets";
import { ENV } from "../../../../utils";
import { BasicModal } from "../../../Shared";
import { useAuth } from "../../../../hooks";
//import { CourseForm} from "../CourseForm";
import "./CourseItem.scss";

const courseController = new Course();

export  function CourseItem(props) {
    const {course, onReload } = props;
    const { accessToken } = useAuth();

    const [showModal, setShowModal] = useState(false);
    const [titleModal, setTitleModal]= useState("");

    const [showConfirm, setShowConfirm] =  useState(false);
    const [confirmMessage, setConfirmMessage]  = useState("");
    
    const [isDelete, setIsDelete] = useState(false);

    const onOpenCloseModal = () => setShowModal((prevState) => !prevState);
    const onOpenCloseConfirm = () => setShowConfirm((prevState) => !prevState);

    const openUpdateUser = () => {
        setTitleModal(`Atualizar ${user.email}`);
        onOpenCloseModal();
    }

    const openDesactivateActivateConfirm = () => {
        setIsDelete(false);
        setConfirmMessage(user.active ? `Desativar usuário ${user.email}` : `Ativar usuário ${user.email}`);
        onOpenCloseConfirm();
    }

    const onActivateDesactivate = async () => {
        try {
            await courseController.updateUser(accessToken, user._id, { active: !user.active});
            onReload();
            onOpenCloseConfirm();
        } catch (error) {
            console.error(error);
        }
    }

    const onDelete = async () => {
        try {
            await courseController.deleteUser(accessToken, user._id);
            onReload();
            onOpenCloseConfirm();
        } catch (error) {
            console.error(error);
        }
    }

    const openDeleteConfirm = () => {
        setIsDelete(true);
        setConfirmMessage(`Excluir usuário ${User.email}?`);
        onOpenCloseConfirm();
    }

  return (
    <>
        <div className='user-item'>
            <div className='user-item__info'>
                <Image avatar src={user.avatar ? `${ENV.BASE_PATH}/${user.avatar}` :  image.noAvatar} />
                <div>
                    <p>{user.firstname} {user.lastname}</p>
                    <p>{user.email}</p>
                </div>
            </div>
       
            <div>
                <Button icon primary>
                    <Icon name= "pencil" onClick={openUpdateUser}/>
                </Button>
                <Button icon color={user.active ? "orange" : "teal"}  onClick={openDesactivateActivateConfirm}>
                    <Icon name={user.active ? "ban": "check"} />
                </Button>
                <Button icon color="red" onClick={openDeleteConfirm}>
                    <Icon name= "trash"/>
                </Button>
            </div>
        </div>

        <BasicModal show={showModal} close={onOpenCloseModal} title={titleModal}>
           <UserForm close={onOpenCloseModal} onReload={onReload} user={user} />
        </BasicModal> 

        <Confirm 
            open={showConfirm}
            onCancel={onOpenCloseConfirm}
            onConfirm={isDelete ? onDelete : onActivateDesactivate }
            content={confirmMessage}
            size="mini"
        />

    </>
  )
}
