import React from 'react';
import { Form, Image } from "semantic-ui-react";
import { useFormik } from "formik";
import { initialValues, validationSchema } from "./UserForm.form";

export function UsersForm(props) {
  
  const { close, onReload, user } = props;

  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: validationSchema(),
    validateOnChange: false,
    onSubmit: async (formValue)
  })


  return (
    <Form className='user-form'>
        <div className='user-form__avatar'>
            <span>AVATAR</span>
        </div>

    <Form.Group widths="equal">
        <Form.Input name="firstname" palceholder="Nome" />
        <Form.Input name="lastname" palceholder="Sobrenome" />
    </Form.Group>


    <Form.Group widths="equal">
        <Form.Input name="email" palceholder="EE-mail" />
        <Form.Dropdown placeholder='Selecione um perfil!' options={roleOptions} selection />
    </Form.Group>

    <Form.Input type="password" name="password" placeholder="Contasenha" />

    <Form.Button type="submit"  primary fluid>
        {user ? "Atualizar usuário" : "Criar usuário"}
    </Form.Button>

    </Form>
  );
}

const roleOptions = [
    {
        key: "user",
        text: "Usuário",
        value: "user",
    },

    {
        key: "user",
        text: "Administrador",
        value: "admin",
    },
];
