import React, {useCallback} from 'react';
import { Form, Image } from "semantic-ui-react";
import { useFormik } from "formik";
import { useDropzone } from "react-dropzone";
import { Course } from "../../../../api";
import { useAuth } from "../../../../hooks";
import { image } from "../../../../assets";
import { ENV } from "../../../../utils";
import { initialValues, validationSchema } from "./CourseForm.form";
import "./CourseForm.scss";


const courseController = new Course();

export function CourseForm(props) {
  
  const { close, onReload, course } = props;
  //console.log(props);
//console.log(onReload());
  //console.log(close);
  const { accessToken } = useAuth();

  const formik = useFormik({
    initialValues: initialValues(course),
    validationSchema: validationSchema(),
    validateOnChange: false,
    onSubmit: async (formValue) => {
        try {
            if(!course){
                await courseController.createCourse(accessToken, formValue);
            }else{
               await courseController.updateUser(accessToken, course._id , formValue);
            }
            onReload();
            close();
        } catch (error) {
            console.error(error);
        }
    }
  });

  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    formik.setFieldValue("avatar", URL.createObjectURL(file));
    formik.setFieldValue("fileAvatar", file);
  });

  const { getRootProps, getInputProps } = useDropzone({
    accept: "imag/jpeg, image/png",
    onDrop,
  });

  const getMiniature = () => {
    if(formik.values.file){
        return formik.values.miniature;
    }else if(formik.values.miniature){
        return `${ENV.BASE_PATH}/${formik.values.miniature}`;
    }
    return image.noAvatar;
  }


  return (
    <Form className='course-form' onSubmit={formik.handleSubmit}>
        <div className='course-form__miniature' {...getRootProps()}>
            <input {...getInputProps()} />
            <Image avatar size="small" src={getMiniature()} />
        </div>

    <Form.Group widths="equal">
        <Form.Input 
            name="title" 
            placeholder="Título"
            onChange={formik.handleChange}
            value={formik.values.title}
            error={formik.errors.title}
        />
        <Form.Input 
            name="description" 
            placeholder="Descrição" 
            onChange={formik.handleChange}
            value={formik.values.description}
            error={formik.errors.description}
        />
    </Form.Group>


    <Form.Group widths="equal">
        <Form.Input 
            name="price" 
            placeholder="Preço"
            onChange={formik.handleChange}
            value={formik.values.price}
            error={formik.errors.price}
        />
        <Form.Input 
            name="score" 
            placeholder="Avaliação"
            onChange={formik.handleChange}
            value={formik.values.score}
            error={formik.errors.score}
        />
        {/* <Form.Dropdown 
            placeholder='Selecione um perfil!' 
            options={roleOptions} 
            selection 
            onChange={(_, data)=> formik.setFieldValue("role", data.value )}
            value={formik.values.role}
            error={formik.errors.role}
        /> */}
    </Form.Group>

    {/* <Form.Input 
        type="password" 
        name="password" 
        placeholder="Senha" 
        onChange={formik.handleChange}
        value={formik.values.password}
        error={formik.errors.password}
    /> */}

    <Form.Button type="submit"  primary fluid  loading={ formik.isSubmitting }>
        {course ? "Atualizar o curso" : "Criar novo curso"}
    </Form.Button>

    </Form>
  );
}

