import React, { useState } from 'react';
import { Image, Button, Icon, Confirm } from 'semantic-ui-react';
import { Course } from '../../../../api';
// import { image } from "../../../../assets";
import { ENV } from "../../../../utils";
import { BasicModal } from "../../../Shared";
import { useAuth } from "../../../../hooks";
//import { PostForm} from "../PostForm";
import "./PostItem.scss";

const courseController = new Course();

export  function PostItem(props) {
    const {course, onReload } = props;
    const { accessToken } = useAuth();

    const [showModal, setShowModal] = useState(false);
    const [titleModal, setTitleModal]= useState("");

    const [showConfirm, setShowConfirm] =  useState(false);
    
    const onOpenCloseModal = () => setShowModal((prevState) => !prevState);
    const onOpenCloseConfirm = () => setShowConfirm((prevState) => !prevState);

    const openUpdateCourse = () => {
        setTitleModal(`Atualizar ${course.title}`);
        onOpenCloseModal();
    }


    const onDelete = async () => {
        try {
            await courseController.deleteCourse(accessToken, course._id);
            onReload();
            onOpenCloseConfirm();
        } catch (error) {
            console.error(error);
        }
    }

  return (
    <>
        <div className='course-item'>
            <div className='course-item__info'>
                <Image avatar src={`${ENV.BASE_PATH}/${course.miniature}`} />
                <div>
                    <p>{course.title}</p>
                    <p>{course.description}</p>
                </div>
            </div>
       
            <div>
                <Button icon as="a" href={course.url} target="_blank">
                    <Icon name="eye" />
                </Button>
                <Button icon primary>
                    <Icon name= "pencil" onClick={openUpdateCourse}/>
                </Button>
                <Button icon color="red" onClick={onOpenCloseConfirm}>
                    <Icon name= "trash"/>
                </Button>
            </div>
        </div>

        <BasicModal show={showModal} close={onOpenCloseModal} title={titleModal}>
           {/* <PostForm close={onOpenCloseModal} onReload={onReload} course={course} /> */}
        </BasicModal> 

        <Confirm 
            open={showConfirm}
            onCancel={onOpenCloseConfirm}
            onConfirm= {onDelete}
            content={`Excluir o curso ${course.title}?`}
            size="mini"
        />

    </>
  )
}
