import { ENV } from "../utils";

export class Post {

    baseApi = ENV.BASE_API;

    async getPosts(accessToken, parameters) {
        try {

            const pageFilter = `page=${parameters?.page || 1}`;
            const limitFilter = `limit=${parameters?.limit || 10}`;
            const url = `${this.baseApi}/${ENV.API_ROUTES.POSTS}?${pageFilter}&${limitFilter}`;
            const params = {
                method: "GET",
                headers: {
                    "Authorization": `Bearer ${accessToken}`,
                },
            };

            const response = await fetch(url, params);
            const result = await response.json();

            if (response.status !== 200) throw result;

            return result;
        } catch (error) {
            throw error;
        }
    }

    async deleteCourse(accessToken, idPost){
        try {
           const url = `${this.baseApi}/${ENV.API_ROUTES.POST}/${idPost}`;
           const params = {
                method: "DELETE",
                headers: {
                    Authorization: `Bearer ${accessToken}`,
                },
           };

           const response = await fetch(url, params);
           const result = await response.json();

           if(response.status !== 200) throw result;

           return result;
        } catch (error) {
            throw error;
        }
    }

}
