import React, { useState } from 'react';
import { Tab, Button } from "semantic-ui-react";
import "./Posts.scss";
//import {  ListPosts, PostForm } from "../../../componentes/Admin/Posts"
import { BasicModal } from "../../../componentes/Shared";

export function Posts() {

  const [showModal, setShowModal] = useState(false);
  const [reload, setReload] = useState(false);

  const onOpenCloseModal = () => setShowModal((prevState) => !prevState );
  const onReload = () => setReload((prevState) => !prevState);

  const panes = [
    {
      menuItem: "Posts",
      render: () => (
        <Tab.Pane attached={false}>
           {/* <ListPosts reload={reload} onReload={onReload}/>  */}
           Posts
        </Tab.Pane>
      ),
    },
  ];

  return (
    <>
      <div className='courses-page'>
        <Button className='courses-page__add' primary onClick={ onOpenCloseModal }>
          Novo curso
        </Button>
        <Tab menu={{ secondary: true }} panes={panes} />
      </div>

      <BasicModal 
        show={ showModal } 
        close={ onOpenCloseModal } 
        title="Criar novo curso"
      >

          <CourseForm close={onOpenCloseModal}  onReload={onReload}/>
      </BasicModal>
    </>
  );
}
