import * as Yup from "yup";

export function initialValues(course){
    return {
        miniature: course?.miniature || "",
        file: null,
        title: course?.title || "",
        description: course?.description || "",
        price: course?.price || null,
        score: course?.score || null,
    };
}


export function validationSchema(){
    return Yup.object({
        title: Yup.string().required('Informe o título!'),
        description: Yup.string().required('Informe o descrição!'),
        price: Yup.number().email(true).required("Informe o preço!"),
        score: Yup.string().required('Selecione a avaliação!'),
    });
}
